class Person {
  name: string;
  age: number;
  job?: string;

  constructor(name: string, age: number) {
    this.name = name;
    this.age = age;
  }

  getName = (): string => {
    return this.name;
  };

  getAge = (): number => {
    return this.age;
  };

  setJob = (job: string): void => {
    this.job = job;
  };
}

class House {
  adress: string;
  price: number;
  residents: Person[];

  constructor(adress: string, price: number, residents: Person[]) {
    this.adress = adress;
    this.price = price;
    this.residents = residents;
  }

  addResident = (resident: Person): void => {
    this.residents.push(resident);
  };
}

class Programmer extends Person {
  company: string;
  salary: number;
  language: string;

  constructor(
    name: string,
    age: number,
    company: string,
    salary: number,
    language: string
  ) {
    super(name, age);
    this.company = company;
    this.salary = salary;
    this.language = language;
  }

  sayHi = (): void => {
    console.log(
      `Hello, I am a programmer! My name is ${this.getName()} I work for ${this.company}`
    );
  };
}
